package net.shutingg.leetCode;

import org.junit.Before;
import org.junit.Test;
import static org.junit.Assert.*;

public class PalindromePartitioning2Tests {
    private PalindromePartitioning2 palindromePartitioning2;
    @Before
    public void setup(){
        palindromePartitioning2 = new PalindromePartitioning2();
    }

    @Test
    public void testMinCut(){
        assertEquals(1, palindromePartitioning2.minCut("aab"));
        assertEquals(1, palindromePartitioning2.minCut("aabbaaaa"));
        assertEquals(1, palindromePartitioning2.minCut(""));
    }
}
